  'publisherNote' => 
  array (
    'en_US' => 'Croatian Geographical Society - Zadar ; Department of Geography, University of Zadar',
    'hr_HR' => '<p>Hrvatsko geografsko društvo - Zadar, Odjel za geografiju, Sveučilište u Zadru<br /> Ul. dr. Franje Tuđmana 24i, 23000 Zadar, Hrvatska</p>',
